   393â†’// Load sample data
   394â†’async function loadSampleData(sampleType: string) {
   395â†’  try {
   396â†’    let filename: string;
   397â†’    
   398â†’    if (sampleType === 'users') {
   399â†’      filename = 'users.json';
   400â†’    } else if (sampleType === 'products') {
