   410â†’    if (!response.ok) {
   411â†’      throw new Error('Failed to load sample data');
   412â†’    }
   413â†’    
   414â†’    const blob = await response.blob();
   415â†’    const file = new File([blob], filename, { type: blob.type });
   416â†’    
   417â†’    // Upload the file
   418â†’    await handleFileUpload(file);
