  display: inline-block;
  background: white;
  padding: 0.25rem 0.5rem;
  border-radius: 4px;
  margin-right: 0.5rem;
  margin-top: 0.25rem;
  border: 1px solid var(--border-color);
}

.column-name {
  font-weight: 500;
  color: var(--text-primary);
}

.column-type {
  color: var(--primary-color);
  font-size: 0.75rem;
  margin-left: 0.25rem;
}

