  height: 2rem;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 4px;
  transition: all 0.2s;
}

.remove-table-button:hover {
  background: rgba(220, 53, 69, 0.1);
  color: var(--error-color);
}

