  margin-bottom: 2rem;
}

.sample-data-section h3 {
  font-size: 1.1rem;
  margin-bottom: 1rem;
}

.sample-buttons {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1rem;
}

.sample-button {
  display: flex;
  align-items: center;
  gap: 1rem;
  padding: 1rem;
  background: #f8f9fa;
  border: 2px solid var(--border-color);
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s;
  text-align: left;
}

.sample-button:hover {
  border-color: var(--primary-color);
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(102, 126, 234, 0.2);
}

.sample-icon {
  font-size: 2rem;
}

.sample-text {
  display: flex;
  flex-direction: column;
}

.sample-text strong {
  font-size: 1rem;
  color: var(--text-primary);
}

.sample-text small {
  font-size: 0.813rem;
  color: var(--text-secondary);
}

.divider {
  text-align: center;
  margin: 2rem 0;
  color: var(--text-secondary);
  position: relative;
}

.divider::before,
.divider::after {
  content: '';
  position: absolute;
  top: 50%;
  width: 45%;
  height: 1px;
  background: var(--border-color);
}

.divider::before {
  left: 0;
}

.divider::after {
  right: 0;
}

