}

.sample-text strong {
  font-size: 1rem;
  color: var(--text-primary);
}

.sample-text small {
  font-size: 0.813rem;
  color: var(--text-secondary);
}

.divider {
  text-align: center;
  margin: 2rem 0;
  color: var(--text-secondary);
  position: relative;
}

.divider::before,
.divider::after {
  content: '';
