  --text-secondary: #495057;
  --border-color: #e0e0e0;
  --error-color: #dc3545;
  --success-color: #28a745;
  --code-background: #f8f9fa;
  --table-hover: #f8f9fa;
  --modal-overlay: rgba(0, 0, 0, 0.5);
  --secondary-button-bg: #ffffff;
