}

.tables-list {
  margin-top: 1rem;
}

.table-item {
  padding: 1rem;
  background: var(--code-background);
  border-radius: 8px;
  margin-bottom: 0.75rem;
  border: 1px solid var(--border-color);
  transition: background-color 0.3s ease, border-color 0.3s ease;
}

.table-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 0.5rem;
}

.table-name {
  font-weight: 600;
  font-size: 1.1rem;
  color: var(--text-primary);
}

.table-info {
  font-size: 0.875rem;
  color: var(--text-secondary);
}

.table-columns {
  font-size: 0.813rem;
  color: var(--text-secondary);
  margin-top: 0.5rem;
}

.column-tag {
  display: inline-block;
  background: var(--surface);
  padding: 0.25rem 0.5rem;
  border-radius: 4px;
  margin-right: 0.5rem;
  margin-top: 0.25rem;
  border: 1px solid var(--border-color);
  transition: background-color 0.3s ease, border-color 0.3s ease;
}

.column-name {
  font-weight: 500;
  color: var(--text-primary);
}

.column-type {
  color: var(--primary-color);
  font-size: 0.75rem;
  margin-left: 0.25rem;
}

.remove-table-button {
  background: none;
  border: none;
  font-size: 1.5rem;
  color: var(--text-secondary);
  cursor: pointer;
  width: 2rem;
  height: 2rem;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 4px;
  transition: all 0.2s;
