  margin: 0 0 1rem 0;
  color: var(--text-secondary);
}

.tables-list {
  margin-top: 1rem;
}

.table-item {
  padding: 1rem;
  background: var(--code-background);
  border-radius: 8px;
  margin-bottom: 0.75rem;
  border: 1px solid var(--border-color);
  transition: background-color 0.3s ease, border-color 0.3s ease;
}

.table-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 0.5rem;
}

.table-name {
  font-weight: 600;
  font-size: 1.1rem;
  color: var(--text-primary);
}

.table-info {
  font-size: 0.875rem;
  color: var(--text-secondary);
}

.table-columns {
  font-size: 0.813rem;
  color: var(--text-secondary);
  margin-top: 0.5rem;
}

.column-tag {
  display: inline-block;
  background: var(--surface);
  padding: 0.25rem 0.5rem;
  border-radius: 4px;
  margin-right: 0.5rem;
  margin-top: 0.25rem;
  border: 1px solid var(--border-color);
  transition: background-color 0.3s ease, border-color 0.3s ease;
}

.column-name {
  font-weight: 500;
  color: var(--text-primary);
}

.column-type {
  color: var(--primary-color);
  font-size: 0.75rem;
  margin-left: 0.25rem;
}

.remove-table-button {
  background: none;
  border: none;
  font-size: 1.5rem;
  color: var(--text-secondary);
  cursor: pointer;
  width: 2rem;
  height: 2rem;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 4px;
  transition: all 0.2s;
}

.remove-table-button:hover {
  background: rgba(220, 53, 69, 0.1);
  color: var(--error-color);
}

/* Loading States */
.loading {
  display: inline-block;
  width: 20px;
  height: 20px;
  border: 3px solid rgba(255, 255, 255, 0.3);
  border-radius: 50%;
  border-top-color: white;
  animation: spin 1s ease-in-out infinite;
}

@keyframes spin {
  to { transform: rotate(360deg); }
}

/* Error States */
.error-message {
  background: rgba(220, 53, 69, 0.1);
  border: 1px solid var(--error-color);
  color: var(--error-color);
  padding: 1rem;
  border-radius: 8px;
  margin: 1rem 0;
}

/* Modal Styles */
.modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: var(--modal-overlay);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
}

.modal-content {
  background: var(--surface);
  border-radius: 16px;
  max-width: 600px;
  width: 90%;
  max-height: 80vh;
  overflow-y: auto;
  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.2);
  transition: background-color 0.3s ease;
}

.modal-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1.5rem;
  border-bottom: 1px solid var(--border-color);
}

.modal-header h2 {
  margin: 0;
}

.close-modal {
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: var(--text-secondary);
  padding: 0;
  width: 2rem;
  height: 2rem;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 4px;
}

.close-modal:hover {
  background: var(--close-button-hover);
}

.modal-body {
  padding: 1.5rem;
}

/* Sample Data Section */
.sample-data-section {
  margin-bottom: 2rem;
}

.sample-data-section h3 {
  font-size: 1.1rem;
  margin-bottom: 1rem;
}

.sample-buttons {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1rem;
}

.sample-button {
  display: flex;
  align-items: center;
  gap: 1rem;
  padding: 1rem;
  background: var(--code-background);
  border: 2px solid var(--border-color);
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s ease;
  text-align: left;
}

.sample-button:hover {
  border-color: var(--primary-color);
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(102, 126, 234, 0.2);
}

.sample-icon {
  font-size: 2rem;
}

.sample-text {
  display: flex;
  flex-direction: column;
}

.sample-text strong {
  font-size: 1rem;
  color: var(--text-primary);
}

.sample-text small {
  font-size: 0.813rem;
  color: var(--text-secondary);
}

.divider {
  text-align: center;
  margin: 2rem 0;
  color: var(--text-secondary);
  position: relative;
}

.divider::before,
.divider::after {
  content: '';
  position: absolute;
  top: 50%;
  width: 45%;
  height: 1px;
  background: var(--border-color);
}

.divider::before {
  left: 0;
}

.divider::after {
  right: 0;
}

